const { URL } = require('url');
const querystring = require('querystring');
var processUrl = process.argv[2];
var myUrl = new URL(processUrl);


function analyseUrl(url){
    console.log('The protocol is:', url.protocol);
    console.log('The host is:', url.host);
    console.log('The hostname is: ', url.hostname);
    console.log('The port is: ', url.port);
    console.log('The path is: ', url.pathname+myUrl.search);
    console.log('The pathname is:', url.pathname);
    console.log('The query is', url.search.slice(1));
    //parameters
    var values = querystring.parse(url.search.slice(1));
    for(key in values){
        console.log("The value of parameter " + key + " = " + values[key]);
    }
}
analyseUrl(myUrl);
